import { useRef, useState } from 'react';

import Card from '../UI/Card';
import LoadingSpinner from '../UI/LoadingSpinner';
import classes from './QuoteForm.module.css';

const QuoteForm = (props) => {
  const authorInputRef = useRef();
  const textInputRef = useRef();

  const [formIsValid, setFormIsValid] = useState('false');
  const [authourOnChange, setAuthorOnChange] = useState('')


  console.log(authourOnChange)
  const handleAuthorChange = (e)=>{
    setAuthorOnChange(e.target.value)
  }  

  if (authourOnChange.trim().length>0){
    setFormIsValid(true)
  }

  function submitFormHandler(event) {
    event.preventDefault();

    const enteredAuthor = authorInputRef.current.value;
    const enteredText = textInputRef.current.value;

    // optional: Could validate here

    props.onAddQuote({ author: enteredAuthor, text: enteredText });
    authorInputRef.current.value ="";
    textInputRef.current.value="";
  }

  return (
    <Card>
      <form className={classes.form} onSubmit={submitFormHandler}>
        {props.isLoading && (
          <div className={classes.loading}>
            <LoadingSpinner />
          </div>
        )}

        <div className={classes.control}>
          <label htmlFor='author'>Author</label>
          <input type='text' id='author' onChange={handleAuthorChange}/>
        </div>
        <div className={classes.control}>
          <label htmlFor='text'>Text</label>
          <textarea id='text' rows='5' ref={textInputRef}></textarea>
        </div>
        <div className={classes.actions}>
          <button className='btn' disabled={!formIsValid}>Add Quote</button>
        </div>
      </form>
    </Card>
  );
};

export default QuoteForm;
